'use client'

import { useEffect } from 'react'
import { Button } from '@/components/ui/button'
import { Card } from '@/components/ui/card'
import { AlertTriangle, RefreshCw } from 'lucide-react'

export default function MobileError({
    error,
    reset,
}: {
    error: Error & { digest?: string }
    reset: () => void
}) {
    useEffect(() => {
        console.error('Mobile Error:', error)
    }, [error])

    return (
        <div className="flex flex-col items-center justify-center h-full min-h-[50vh] p-6 text-center bg-[var(--color-bg-primary)]">
            <Card className="p-8 max-w-sm w-full shadow-card">
                <div className="status-danger w-fit p-3 rounded-full mb-4 mx-auto">
                    <AlertTriangle className="h-6 w-6" />
                </div>
                <h2 className="text-lg font-bold text-[var(--color-text-primary)] mb-2">Something went wrong</h2>
                <p className="text-[var(--color-text-secondary)] text-sm mb-6">
                    {error.message || 'An unexpected error occurred.'}
                </p>
                <Button
                    onClick={reset}
                    size="sm"
                    className="gap-2 shadow-card hover:shadow-card-hover"
                >
                    <RefreshCw className="h-4 w-4" />
                    Try Again
                </Button>
            </Card>
        </div>
    )
}

'use client'

import { LucideIcon } from 'lucide-react'
import { cn } from '@/lib/utils'

interface BigButtonProps {
  icon?: LucideIcon
  label: string
  sublabel?: string
  variant?: 'default' | 'primary' | 'success' | 'warning' | 'danger'
  disabled?: boolean
  onClick?: () => void
  className?: string
}

const variantStyles = {
  default: 'bg-[var(--color-bg-surface)] text-[var(--color-text-primary)] border-[var(--color-border)]',
  primary: 'bg-[var(--color-accent-primary)] text-white border-[var(--color-accent-primary)] shadow-glow',
  success: 'bg-[var(--color-accent-secondary)] text-white border-[var(--color-accent-secondary)]',
  warning: 'bg-amber-600 text-white border-amber-500',
  danger: 'bg-red-600 text-white border-red-500',
}

/**
 * BigButton - Mobile-optimized button for field technicians
 *
 * Features:
 * - 44px minimum height for standard touch target
 * - Large touch target (full width)
 * - High contrast colors
 * - Optional icon and sublabel
 */
export function BigButton({
  icon: Icon,
  label,
  sublabel,
  variant = 'default',
  disabled = false,
  onClick,
  className,
}: BigButtonProps) {
  return (
    <button
      onClick={onClick}
      disabled={disabled}
      className={cn(
        'w-full min-h-[44px] px-6 py-4 rounded-2xl border-2',
        'flex items-center justify-center gap-3',
        'font-bold text-lg uppercase tracking-wide',
        'transition-all duration-200 ease-out',
        'shadow-card hover:shadow-card-hover hover:-translate-y-px',
        'active:scale-[0.98] active:shadow-sm',
        'disabled:opacity-50 disabled:cursor-not-allowed disabled:shadow-sm',
        'relative overflow-hidden',
        // Add subtle top highlight for depth
        'before:absolute before:inset-x-0 before:top-0 before:h-px',
        'before:bg-gradient-to-r before:from-transparent before:via-white/10 before:to-transparent',
        variantStyles[variant],
        className
      )}
    >
      {Icon && <Icon className="w-6 h-6 flex-shrink-0" />}
      <div className="flex flex-col items-start">
        <span>{label}</span>
        {sublabel && (
          <span className="text-xs font-normal opacity-80 normal-case">
            {sublabel}
          </span>
        )}
      </div>
    </button>
  )
}

/**
 * BigButtonGrid - Grid layout for multiple BigButtons
 */
export function BigButtonGrid({ children, columns = 2 }: { children: React.ReactNode; columns?: 1 | 2 }) {
  return (
    <div className={cn(
      'grid gap-4',
      columns === 1 ? 'grid-cols-1' : 'grid-cols-2'
    )}>
      {children}
    </div>
  )
}

'use client'

import { useEffect, useRef, useState } from 'react'
import { useRouter } from 'next/navigation'
import { createClient } from '@/lib/supabase/client'
import type { RealtimeChannel } from '@supabase/supabase-js'

// Default account ID - matches the Supabase MCP server default
// TODO: Make this dynamic based on logged-in user
const DEFAULT_ACCOUNT_ID = 'fde73a6a-ea84-46a7-803b-a3ae7cc09d00'

// Extend Window interface for Web Speech API
declare global {
  interface Window {
    SpeechRecognition: any
    webkitSpeechRecognition: any
  }
}

/**
 * Hook that listens for voice navigation commands from the ElevenLabs agent
 * via Supabase Realtime and executes them by navigating to the requested page.
 * Also provides voice button controls for manual voice input.
 *
 * @param accountId - The account ID to listen for commands (defaults to DEFAULT_ACCOUNT_ID)
 */
export function useVoiceNavigation(accountId: string = DEFAULT_ACCOUNT_ID) {
  const router = useRouter()
  const channelRef = useRef<RealtimeChannel | null>(null)
  const recognitionRef = useRef<any>(null)
  const supabase = createClient()
  const [isListening, setIsListening] = useState(false)

  useEffect(() => {
    // Always subscribe - we have a default account ID
    if (!accountId) return // Safety check, but should always have value

    // Subscribe to new navigation commands
    const channel = supabase
      .channel(`voice-nav-${accountId}`)
      .on(
        'postgres_changes',
        {
          event: 'INSERT',
          schema: 'public',
          table: 'voice_navigation_commands',
          filter: `account_id=eq.${accountId}`,
        },
        async (payload) => {
          const command = payload.new as {
            id: string
            page: string
            params: Record<string, any>
            executed: boolean
          }

          // Skip if already executed
          if (command.executed) return

          console.log('[VoiceNavigation] Received command:', command.page)

          // Navigate to the page
          router.push(command.page)

          // Mark command as executed
          await supabase
            .from('voice_navigation_commands')
            .update({
              executed: true,
              executed_at: new Date().toISOString()
            })
            .eq('id', command.id)

          console.log('[VoiceNavigation] Navigated to:', command.page)
        }
      )
      .subscribe((status) => {
        console.log('[VoiceNavigation] Subscription status:', status)
      })

    channelRef.current = channel

    // Cleanup on unmount
    return () => {
      if (channelRef.current) {
        supabase.removeChannel(channelRef.current)
      }
      if (recognitionRef.current) {
        recognitionRef.current.stop()
      }
    }
  }, [accountId, router, supabase])

  const startListening = () => {
    // Check for Web Speech API support
    if (globalThis.window === undefined) return
    if (!('webkitSpeechRecognition' in globalThis) && !('SpeechRecognition' in globalThis)) {
      alert('Voice input not supported in this browser')
      return
    }

    const SpeechRecognition = globalThis.SpeechRecognition || globalThis.webkitSpeechRecognition
    const recognition = new SpeechRecognition()

    recognition.continuous = true
    recognition.interimResults = false
    recognition.lang = 'en-US'

    recognition.onstart = () => {
      setIsListening(true)
      console.log('[VoiceNavigation] Started listening')
    }

    recognition.onresult = (event: any) => {
      const transcript = event.results[event.results.length - 1][0].transcript.toLowerCase()
      console.log('[VoiceNavigation] Voice input:', transcript)

      // Here you could add command parsing logic
      // For now, just log the transcript
      // In the future, this could trigger navigation or actions
    }

    recognition.onerror = (event: any) => {
      console.error('[VoiceNavigation] Speech recognition error:', event.error)
      setIsListening(false)
    }

    recognition.onend = () => {
      setIsListening(false)
      console.log('[VoiceNavigation] Stopped listening')
    }

    recognitionRef.current = recognition
    recognition.start()
  }

  const stopListening = () => {
    if (recognitionRef.current) {
      recognitionRef.current.stop()
      recognitionRef.current = null
    }
    setIsListening(false)
  }

  return {
    isListening,
    startListening,
    stopListening,
  }
}

/**
 * Provider component that enables voice navigation globally
 * Place this in your root layout or dashboard layout
 */
export function VoiceNavigationProvider({
  accountId = DEFAULT_ACCOUNT_ID
}: {
  accountId?: string
}) {
  useVoiceNavigation(accountId)
  return null
}
