messages.content:
@tailwind base;
@tailwind components;
@tailwind utilities;

/* ============================================
   TYPOGRAPHY - Inter Font System
   Clean, professional SaaS typography
   ============================================ */

html {
  font-feature-settings: 'cv02', 'cv03', 'cv04', 'cv11';
  -webkit-font-smoothing: antialiased;
  -moz-osx-font-smoothing: grayscale;
}

/* Typography Scale */
h1, .h1 {
  font-weight: 700;
  letter-spacing: -0.025em;
  line-height: 1.1;
}

h2, .h2 {
  font-weight: 700;
  letter-spacing: -0.025em;
  line-height: 1.2;
}

h3, .h3 {
  font-weight: 600;
  letter-spacing: -0.015em;
  line-height: 1.3;
}

h4, .h4 {
  font-weight: 600;
  letter-spacing: -0.01em;
  line-height: 1.4;
}

/* Body text improvements */
p, .body-text {
  line-height: 1.6;
}

/* Small text */
.text-sm, small {
  line-height: 1.5;
}

:root {
  --radius: 0.5rem;

  /* ============================================
     WARM MODE (LIGHT THEME) - "LATTE"
     A sophisticated, grounded palette using earthy neutrals and warm accents.
     Pillars: Cream, Sand, Bronze, Clay, Teal.
     ============================================ */

  /* Core Colors */
  --warm-bg-primary: #FDF8F5;      /* Warm Cream Base */
  --warm-bg-secondary: #F5EBE0;    /* Sand Secondary */
  --warm-bg-surface: #FFFDF9;      /* Lighter Surface */
  --warm-text-primary: #422006;    /* Dark Bronze Text */
  --warm-text-secondary: #78350F;  /* Medium Bronze */
  --warm-text-subtle: #92400E;     /* Light Bronze */
  --warm-border: #E7D5C9;          /* Soft border */

  /* Accents & Gradients */
  --warm-accent-primary: #EA580C;  /* Burnt Orange */
  --warm-accent-secondary: #0F766E; /* Deep Teal (Complementary) */
  --warm-gradient-accent: linear-gradient(135deg, #EA580C, #D97706);
  --warm-gradient-surface: linear-gradient(155deg, rgba(234, 88, 12, 0.08), rgba(217, 119, 6, 0.04));

  /* Glass Effects */
  --warm-glass-border: rgba(231, 213, 201, 0.5);
  --warm-glass-highlight: rgba(255, 255, 255, 0.4);
  --warm-glass-overlay: radial-gradient(circle at top left, rgba(253, 248, 245, 0.4), transparent 60%);

  /* Shadows */
  --warm-shadow-elevated: 0 20px 50px -20px rgba(66, 32, 6, 0.15);
  --warm-shadow-glow: 0 0 30px -10px rgba(234, 88, 12, 0.3);
  --warm-shadow-sm: 0 4px 6px -1px rgba(66, 32, 6, 0.05);

  /* Component Specific */
  --warm-card-bg: #FFFFFF;
  --warm-card-border: #E7D5C9;
  --warm-input-bg: #FDF8F5;
  --warm-badge-bg: #FFEDD5;
  --warm-badge-text: #9A3412;

  /* ============================================
     MIDNIGHT MODE (DARK THEME) - "ABYSS"
     Professional, deep, and focused.
     Pillars: Deep Navy, Slate, Electric Blue, Indigo, Amber.
     ============================================ */

  /* Core Colors */
  --midnight-bg-primary: #020617;     /* Deepest Navy */
  --midnight-bg-secondary: #0F172A;   /* Slate 900 */
  --midnight-bg-surface: #1E293B;     /* Slate 800 */
  --midnight-text-primary: #F8FAFC;   /* Slate 50 */
  --midnight-text-secondary: #94A3B8; /* Slate 400 */
  --midnight-text-subtle: #64748B;    /* Slate 500 */
  --midnight-border: #1E293B;         /* Slate 800 */

  /* Accents & Gradients */
  --midnight-accent-primary: #3B82F6;   /* Blue 500 */
  --midnight-accent-secondary: #F59E0B; /* Amber 500 (Complementary Pop) */
  --midnight-gradient-accent: linear-gradient(135deg, #3B82F6, #2563EB);
  --midnight-gradient-surface: linear-gradient(155deg, rgba(59, 130, 246, 0.1), rgba(37, 99, 235, 0.05));

  /* Glass Effects */
  --midnight-glass-border: rgba(255, 255, 255, 0.1);
  --midnight-glass-highlight: rgba(255, 255, 255, 0.05);
  --midnight-glass-overlay: radial-gradient(circle at top left, rgba(59, 130, 246, 0.15), transparent 60%);

  /* Shadows */
  --midnight-shadow-elevated: 0 25px 50px -12px rgba(0, 0, 0, 0.5);
  --midnight-shadow-glow: 0 0 40px -10px rgba(59, 130, 246, 0.4);
  --midnight-shadow-sm: 0 4px 6px -1px rgba(0, 0, 0, 0.3);

  /* Component Specific */
  --midnight-card-bg: #0F172A;
  --midnight-card-border: #1E293B;
  --midnight-input-bg: #020617;
  --midnight-badge-bg: rgba(59, 130, 246, 0.1);
  --midnight-badge-text: #60A5FA;

  /* ============================================
     LIGHT THEME - "SOLARIS" ‚òÄÔ∏è
     Energetic, bright, and inviting.
     Pillars: White, Light Grey, Orange, Yellow, Rose.
     ============================================ */

  --light-bg-primary: #FFFFFF;
  --light-bg-secondary: #F3F4F6;
  --light-bg-surface: #F9FAFB;
  --light-text-primary: #111827;
  --light-text-secondary: #4B5563;
  --light-text-subtle: #9CA3AF;
  --light-border: #E5E7EB;
  --light-accent-primary: #F97316;   /* Orange 500 */
  --light-accent-secondary: #EAB308; /* Yellow 500 */
  --light-gradient-accent: linear-gradient(135deg, #F97316, #EAB308);
  --light-gradient-surface: linear-gradient(155deg, rgba(249, 115, 22, 0.08), rgba(234, 179, 8, 0.04));
  --light-card-bg: #FFFFFF;
  --light-card-border: #E5E7EB;
  --light-input-bg: #F9FAFB;
  --light-badge-bg: #FFF7ED;
  --light-badge-text: #C2410C;
  --light-shadow-elevated: 0 20px 25px -5px rgba(0, 0, 0, 0.1), 0 10px 10px -5px rgba(0, 0, 0, 0.04);
  --light-shadow-glow: 0 0 20px rgba(249, 115, 22, 0.2);
  --light-shadow-sm: 0 1px 2px 0 rgba(0, 0, 0, 0.05);

  /* ============================================
     DARK THEME - "OPUS" (Claude Style)
     Warm, matte, and literary.
     Pillars: Charcoal, Warm Grey, Cream, Terracotta.
     ============================================ */

  --dark-bg-primary: #211F1E;    /* Warm Charcoal */
  --dark-bg-secondary: #191817;  /* Darker Sidebar */
  --dark-bg-surface: #2D2B29;    /* Input/Card */
  --dark-text-primary: #F0EFEA;  /* Cream White */
  --dark-text-secondary: #9F9D96; /* Warm Grey */
  --dark-text-subtle: #706E68;    /* Darker Warm Grey */
  --dark-border: #3E3D39;
  --dark-accent-primary: #D97757; /* Terracotta */
  --dark-accent-secondary: #C5A289; /* Sand */
  --dark-gradient-accent: linear-gradient(135deg, #D97757, #C5A289);
  --dark-gradient-surface: linear-gradient(155deg, rgba(217, 119, 87, 0.05), rgba(197, 162, 137, 0.02));
  --dark-card-bg: #2D2B29;
  --dark-card-border: #3E3D39;
  --dark-input-bg: #211F1E;
  --dark-badge-bg: rgba(217, 119, 87, 0.1);
  --dark-badge-text: #E6B098;
  --dark-shadow-elevated: 0 25px 50px -12px rgba(0, 0, 0, 0.5);
  --dark-shadow-glow: 0 0 30px rgba(217, 119, 87, 0.1); /* Subtle warm glow */
  --dark-shadow-sm: 0 4px 6px -1px rgba(0, 0, 0, 0.3);




  /* ============================================
     SHARED PROPERTIES
     ============================================ */

  --card-radius: 1rem;
  --button-radius: 0.5rem;
  --input-radius: 0.5rem;
  --badge-radius: 9999px;

  /* Transitions */
  --transition-fast: 150ms cubic-bezier(0.4, 0, 0.2, 1);
  --transition-base: 300ms cubic-bezier(0.4, 0, 0.2, 1);
  --transition-slow: 500ms cubic-bezier(0.4, 0, 0.2, 1);

  /* Backdrop Filters */
  --backdrop-blur: blur(16px);

  /* Z-Index Scale */
  --z-base: 1;
  --z-dropdown: 1000;
  --z-sticky: 1020;
  --z-modal: 1030;
  --z-popover: 1040;
  --z-tooltip: 1050;
}

/* ============================================
   ACTIVE THEME VARIABLES - Default to Warm
   ============================================ */

:root {
  --color-bg-primary: var(--warm-bg-primary);
  --color-bg-secondary: var(--warm-bg-secondary);
  --color-bg-surface: var(--warm-bg-surface);
  --color-text-primary: var(--warm-text-primary);
  --color-text-secondary: var(--warm-text-secondary);
  --color-text-subtle: var(--warm-text-subtle);
  --color-border: var(--warm-border);
  --color-accent-primary: var(--warm-accent-primary);
  --color-accent-secondary: var(--warm-accent-secondary);
  --gradient-accent: var(--warm-gradient-accent);
  --gradient-surface: var(--warm-gradient-surface);
  --glass-border: var(--warm-glass-border);
  --glass-highlight: var(--warm-glass-highlight);
  --glass-overlay: var(--warm-glass-overlay);
  --shadow-elevated: var(--warm-shadow-elevated);
  --shadow-glow: var(--warm-shadow-glow);
  --shadow-sm: var(--warm-shadow-sm);
  --card-bg: var(--warm-card-bg);
  --card-border: var(--warm-card-border);
  --input-bg: var(--warm-input-bg);
  --badge-bg: var(--warm-badge-bg);
  --badge-text: var(--warm-badge-text);

  color-scheme: light;
}

/* Midnight Theme Active */
[data-theme="midnight"] {
  --color-bg-primary: var(--midnight-bg-primary);
  --color-bg-secondary: var(--midnight-bg-secondary);
  --color-bg-surface: var(--midnight-bg-surface);
  --color-text-primary: var(--midnight-text-primary);
  --color-text-secondary: var(--midnight-text-secondary);
  --color-text-subtle: var(--midnight-text-subtle);
  --color-border: var(--midnight-border);
  --color-accent-primary: var(--midnight-accent-primary);
  --color-accent-secondary: var(--midnight-accent-secondary);
  --gradient-accent: var(--midnight-gradient-accent);
  --gradient-surface: var(--midnight-gradient-surface);
  --glass-border: var(--midnight-glass-border);
  --glass-highlight: var(--midnight-glass-highlight);
  --glass-overlay: var(--midnight-glass-overlay);
  --shadow-elevated: var(--midnight-shadow-elevated);
  --shadow-glow: var(--midnight-shadow-glow);
  --shadow-sm: var(--midnight-shadow-sm);
  --card-bg: var(--midnight-card-bg);
  --card-border: var(--midnight-card-border);
  --input-bg: var(--midnight-input-bg);
  --badge-bg: var(--midnight-badge-bg);
  --badge-text: var(--midnight-badge-text);

  color-scheme: dark;
}

/* Warm Theme Active */
[data-theme="warm"] {
  --color-bg-primary: var(--warm-bg-primary);
  --color-bg-secondary: var(--warm-bg-secondary);
  --color-bg-surface: var(--warm-bg-surface);
  --color-text-primary: var(--warm-text-primary);
  --color-text-secondary: var(--warm-text-secondary);
  --color-text-subtle: var(--warm-text-subtle);
  --color-border: var(--warm-border);
  --color-accent-primary: var(--warm-accent-primary);
  --color-accent-secondary: var(--warm-accent-secondary);
  --gradient-accent: var(--warm-gradient-accent);
  --gradient-surface: var(--warm-gradient-surface);
  --glass-border: var(--warm-glass-border);
  --glass-highlight: var(--warm-glass-highlight);
  --glass-overlay: var(--warm-glass-overlay);
  --shadow-elevated: var(--warm-shadow-elevated);
  --shadow-glow: var(--warm-shadow-glow);
  --shadow-sm: var(--warm-shadow-sm);
  --card-bg: var(--warm-card-bg);
  --card-border: var(--warm-card-border);
  --input-bg: var(--warm-input-bg);
  --badge-bg: var(--warm-badge-bg);
  --badge-text: var(--warm-badge-text);

  color-scheme: light;
}

/* System Theme - Auto-detection */
@media (prefers-color-scheme: dark) {
  [data-theme="system"] {
    --color-bg-primary: var(--midnight-bg-primary);
    --color-bg-secondary: var(--midnight-bg-secondary);
    --color-bg-surface: var(--midnight-bg-surface);
    --color-text-primary: var(--midnight-text-primary);
    --color-text-secondary: var(--midnight-text-secondary);
    --color-text-subtle: var(--midnight-text-subtle);
    --color-border: var(--midnight-border);
    --color-accent-primary: var(--midnight-accent-primary);
    --color-accent-secondary: var(--midnight-accent-secondary);
    --gradient-accent: var(--midnight-gradient-accent);
    --gradient-surface: var(--midnight-gradient-surface);
    --shadow-elevated: var(--midnight-shadow-elevated);
    --shadow-glow: var(--midnight-shadow-glow);
    --shadow-sm: var(--midnight-shadow-sm);
    --card-bg: var(--midnight-card-bg);
    --card-border: var(--midnight-card-border);
    --input-bg: var(--midnight-input-bg);
    --badge-bg: var(--midnight-badge-bg);
    --badge-text: var(--midnight-badge-text);

    color-scheme: dark;
  }
}

@media (prefers-color-scheme: light) {
  [data-theme="system"] {
    --color-bg-primary: var(--light-bg-primary);
    --color-bg-secondary: var(--light-bg-secondary);
    --color-bg-surface: var(--light-bg-surface);
    --color-text-primary: var(--light-text-primary);
    --color-text-secondary: var(--light-text-secondary);
    --color-text-subtle: var(--light-text-subtle);
    --color-border: var(--light-border);
    --color-accent-primary: var(--light-accent-primary);
    --color-accent-secondary: var(--light-accent-secondary);
    --gradient-accent: var(--light-gradient-accent);
    --gradient-surface: var(--light-gradient-surface);
    --shadow-elevated: var(--light-shadow-elevated);
    --shadow-glow: var(--light-shadow-glow);
    --shadow-sm: var(--light-shadow-sm);
    --card-bg: var(--light-card-bg);
    --card-border: var(--light-card-border);
    --input-bg: var(--light-input-bg);
    --badge-bg: var(--light-badge-bg);
    --badge-text: var(--light-badge-text);

    color-scheme: light;
  }
}

/* Light Theme Active */
[data-theme="light"] {
  --color-bg-primary: var(--light-bg-primary);
  --color-bg-secondary: var(--light-bg-secondary);
  --color-bg-surface: var(--light-bg-surface);
  --color-text-primary: var(--light-text-primary);
  --color-text-secondary: var(--light-text-secondary);
  --color-text-subtle: var(--light-text-subtle);
  --color-border: var(--light-border);
  --color-accent-primary: var(--light-accent-primary);
  --color-accent-secondary: var(--light-accent-secondary);
  --gradient-accent: var(--light-gradient-accent);
  --gradient-surface: var(--light-gradient-surface);
  --shadow-elevated: var(--light-shadow-elevated);
  --shadow-glow: var(--light-shadow-glow);
  --shadow-sm: var(--light-shadow-sm);
  --card-bg: var(--light-card-bg);
  --card-border: var(--light-card-border);
  --input-bg: var(--light-input-bg);
  --badge-bg: var(--light-badge-bg);
  --badge-text: var(--light-badge-text);

  color-scheme: light;
}

/* Dark Theme Active */
[data-theme="dark"] {
  --color-bg-primary: var(--dark-bg-primary);
  --color-bg-secondary: var(--dark-bg-secondary);
  --color-bg-surface: var(--dark-bg-surface);
  --color-text-primary: var(--dark-text-primary);
  --color-text-secondary: var(--dark-text-secondary);
  --color-text-subtle: var(--dark-text-subtle);
  --color-border: var(--dark-border);
  --color-accent-primary: var(--dark-accent-primary);
  --color-accent-secondary: var(--dark-accent-secondary);
  --gradient-accent: var(--dark-gradient-accent);
  --gradient-surface: var(--dark-gradient-surface);
  --shadow-elevated: var(--dark-shadow-elevated);
  --shadow-glow: var(--dark-shadow-glow);
  --shadow-sm: var(--dark-shadow-sm);
  --card-bg: var(--dark-card-bg);
  --card-border: var(--dark-card-border);
  --input-bg: var(--dark-input-bg);
  --badge-bg: var(--dark-badge-bg);
  --badge-text: var(--dark-badge-text);

  color-scheme: dark;
}




/* ============================================
   OPS THEME - Service Industry Professional
   Pillars: Deep Navy, Slate, Orange, Dark Mode
   ============================================ */

--ops-bg-primary: #0f1115;     /* Deepest Navy */
--ops-bg-secondary: #151820;   /* Slate 900 */
--ops-bg-surface: #1c2029;     /* Slate 800 */
--ops-text-primary: #f5f5f7;   /* Slate 50 */
--ops-text-secondary: #9ca3af; /* Slate 400 */
--ops-text-subtle: #64748b;    /* Slate 500 */
--ops-border: #262b36;         /* Custom border */
--ops-accent-primary: #ff6a3c; /* Burnt Orange */
--ops-accent-secondary: #22c55e; /* Green 500 */
--ops-gradient-accent: linear-gradient(135deg, #ff6a3c, #ea580c);
--ops-gradient-surface: linear-gradient(155deg, rgba(255, 106, 60, 0.1), rgba(234, 88, 12, 0.05));
--ops-card-bg: #1c2029;
--ops-card-border: #262b36;
--ops-input-bg: #0f1115;
--ops-badge-bg: rgba(255, 106, 60, 0.1);
