/* ============================================
   DISPATCH MAP ANIMATIONS
   ============================================ */

/* Slide-in from right (desktop panel) */
@keyframes slideInRight {
  from {
    transform: translateX(100%);
    opacity: 0;
  }
  to {
    transform: translateX(0);
    opacity: 1;
  }
}

.animate-slide-in-right {
  animation: slideInRight 300ms cubic-bezier(0.4, 0, 0.2, 1);
}

/* Slide-in from bottom (mobile panel) */
@keyframes slideInBottom {
  from {
    transform: translateY(100%);
    opacity: 0;
  }
  to {
    transform: translateY(0);
    opacity: 1;
  }
}

.animate-slide-in-bottom {
  animation: slideInBottom 300ms cubic-bezier(0.4, 0, 0.2, 1);
}

/* Fade in (backdrop) */
@keyframes fadeIn {
  from {
    opacity: 0;
  }
  to {
    opacity: 1;
  }
}

.animate-fade-in {
  animation: fadeIn 200ms cubic-bezier(0.4, 0, 0.2, 1);
}

'use client'

import { LucideIcon } from 'lucide-react'
import { cn } from '@/lib/utils'

interface BigButtonProps {
  icon?: LucideIcon
  label: string
  sublabel?: string
  variant?: 'default' | 'primary' | 'success' | 'warning' | 'danger'
  disabled?: boolean
  onClick?: () => void
  className?: string
}

const variantStyles = {
  default: 'bg-[var(--color-bg-surface)] text-[var(--color-text-primary)] border-[var(--color-border)]',
  primary: 'bg-[var(--color-accent-primary)] text-white border-[var(--color-accent-primary)] shadow-glow',
  success: 'bg-[var(--color-accent-secondary)] text-white border-[var(--color-accent-secondary)]',
  warning: 'bg-amber-600 text-white border-amber-500',
  danger: 'bg-red-600 text-white border-red-500',
}

/**
 * BigButton - Mobile-optimized button for field technicians
 *
 * Features:
 * - 44px minimum height for standard touch target
 * - Large touch target (full width)
 * - High contrast colors
 * - Optional icon and sublabel
 */
export function BigButton({
  icon: Icon,
  label,
  sublabel,
  variant = 'default',
  disabled = false,
  onClick,
  className,
}: BigButtonProps) {
  return (
    <button
      onClick={onClick}
      disabled={disabled}
      className={cn(
        'w-full min-h-[44px] px-6 py-4 rounded-2xl border-2',
        'flex items-center justify-center gap-3',
        'font-bold text-lg uppercase tracking-wide',
        'transition-all duration-200 ease-out',
        'shadow-card hover:shadow-card-hover hover:-translate-y-px',
        'active:scale-[0.98] active:shadow-sm',
        'disabled:opacity-50 disabled:cursor-not-allowed disabled:shadow-sm',
        'relative overflow-hidden',
        // Add subtle top highlight for depth
        'before:absolute before:inset-x-0 before:top-0 before:h-px',
        'before:bg-gradient-to-r before:from-transparent before:via-white/10 before:to-transparent',
        variantStyles[variant],
        className
      )}
    >
      {Icon && <Icon className="w-6 h-6 flex-shrink-0" />}
      <div className="flex flex-col items-start">
        <span>{label}</span>
        {sublabel && (
          <span className="text-xs font-normal opacity-80 normal-case">
            {sublabel}
          </span>
        )}
      </div>
    </button>
  )
}

/**
 * BigButtonGrid - Grid layout for multiple BigButtons
 */
export function BigButtonGrid({ children, columns = 2 }: { children: React.ReactNode; columns?: 1 | 2 }) {
  return (
    <div className={cn(
      'grid gap-4',
      columns === 1 ? 'grid-cols-1' : 'grid-cols-2'
    )}>
      {children}
    </div>
  )
}

'use client'

import { usePathname } from 'next/navigation'
import Link from 'next/link'
import { Home, Briefcase, Map, User, Target, Calendar } from 'lucide-react'

interface NavItem {
  href: string
  icon: any
  label: string
  activePattern: RegExp
}

const techNavItems: NavItem[] = [
  { href: '/m/tech/dashboard', icon: Home, label: 'Home', activePattern: /^\/m\/tech\/dashboard/ },
  { href: '/m/tech/dashboard', icon: Briefcase, label: 'Jobs', activePattern: /^\/m\/tech\/(dashboard|job)/ },
  { href: '/m/tech/map', icon: Map, label: 'Map', activePattern: /^\/m\/tech\/map/ },
  { href: '/m/tech/profile', icon: User, label: 'Profile', activePattern: /^\/m\/tech\/profile/ },
]

const salesNavItems: NavItem[] = [
  { href: '/m/sales/dashboard', icon: Home, label: 'Home', activePattern: /^\/m\/sales\/dashboard/ },
  { href: '/m/sales/leads', icon: Target, label: 'Leads', activePattern: /^\/m\/sales\/leads/ },
  { href: '/m/sales/dashboard', icon: Calendar, label: 'Meetings', activePattern: /^\/m\/sales\/(dashboard|meeting)/ },
  { href: '/m/sales/profile', icon: User, label: 'Profile', activePattern: /^\/m\/sales\/profile/ },
]

export function TechBottomNav() {
  return <BottomNav items={techNavItems} />
}

export function SalesBottomNav() {
  return <BottomNav items={salesNavItems} />
}

function BottomNav({ items }: { items: NavItem[] }) {
  const pathname = usePathname()

  return (
    <nav className="fixed bottom-0 left-0 right-0 bg-[var(--color-bg-secondary)]/95 backdrop-blur-md border-t border-[var(--color-border-primary)] shadow-mobile-nav safe-area-inset-bottom z-50">
      <div className="flex justify-around items-center h-16 relative">
        {/* Subtle top highlight for depth */}
        <div className="absolute top-0 left-0 right-0 h-px bg-gradient-to-r from-transparent via-[var(--color-border-primary)] to-transparent opacity-50"></div>
        {items.map((item) => {
          const isActive = item.activePattern.test(pathname)
          const Icon = item.icon

          return (
            <Link
              key={item.href}
              href={item.href}
              className={`flex flex-col items-center justify-center flex-1 h-full transition-all duration-200 relative ${
                isActive
                  ? 'text-[var(--color-accent-primary)]'
                  : 'text-[var(--color-text-secondary)] hover:text-[var(--color-text-primary)] active:text-[var(--color-text-primary)]'
              }`}
            >
              {/* Active indicator with glow */}
              {isActive && (
                <div className="absolute -top-1 w-8 h-0.5 bg-[var(--color-accent-primary)] rounded-full shadow-glow"></div>
              )}

              {/* Icon with subtle shadow when active */}
              <Icon className={`w-6 h-6 mb-1 transition-all duration-200 ${
                isActive ? 'scale-110 drop-shadow-sm' : ''
              }`} />
              <span className="text-xs font-medium">{item.label}</span>
            </Link>
          )
        })}
      </div>
    </nav>
  )
}

'use client'

import { useState, useEffect } from 'react'
import { MapPin, Clock, Wrench, ChevronRight, Menu } from 'lucide-react'
import { BigButton } from '@/components/mobile/big-button'
import { VoiceButton } from '@/components/mobile/voice-button'
import { MobileSidebar, MobileMenuButton } from '@/components/mobile/mobile-sidebar'
import { Card } from '@/components/ui/card'
import { TechBottomNav } from '@/components/mobile/bottom-nav'
import Link from 'next/link'

interface Job {
  id: string
  description: string
  status: string
  scheduledStart: string
  contact: {
    firstName: string
    lastName: string
    address: string
    phone: string
  }
}

export default function TechDashboard() {
  const [jobs, setJobs] = useState<Job[]>([])
  const [loading, setLoading] = useState(true)
  const [currentJob, setCurrentJob] = useState<Job | null>(null)
  const [sidebarOpen, setSidebarOpen] = useState(false)

  useEffect(() => {
    fetchMyJobs()
  }, [])

  const fetchMyJobs = async () => {
    try {
      const res = await fetch('/api/tech/jobs')
      if (res.ok) {
        const data = await res.json()
        setJobs(data.jobs || [])
        // Find current in-progress job
        const inProgress = data.jobs?.find((j: Job) =>
          j.status === 'in_progress' || j.status === 'en_route'
        )
        setCurrentJob(inProgress || data.jobs?.[0] || null)
      }
    } catch (error) {
      console.error('Failed to fetch jobs:', error)
    } finally {
      setLoading(false)
    }
  }

  const formatTime = (dateStr: string) => {
    return new Date(dateStr).toLocaleTimeString('en-US', {
      hour: 'numeric',
      minute: '2-digit',
    })
  }

  if (loading) {
    return (
      <div className="min-h-screen bg-[var(--color-bg-primary)] flex items-center justify-center">
        <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-[var(--color-accent-primary)]" />
      </div>
    )
  }

  return (
    <div className="min-h-screen bg-[var(--color-bg-primary)] text-[var(--color-text-primary)] p-4 pb-24">
      {/* Mobile Sidebar */}
      <MobileSidebar isOpen={sidebarOpen} onClose={() => setSidebarOpen(false)} role="tech" />

      {/* Header */}
      <header className="mb-6 flex items-center justify-between">
        <div>
          <h1 className="text-2xl font-bold">My Jobs</h1>
          <p className="text-[var(--color-text-secondary)]">
            {new Date().toLocaleDateString('en-US', {
              weekday: 'long',
              month: 'short',
              day: 'numeric'
            })}
          </p>
        </div>
        <MobileMenuButton onClick={() => setSidebarOpen(true)} />
      </header>

      {/* Current Job Card */}
      {currentJob && (
        <Card className="p-4 mb-6 border-2 border-[var(--color-accent-primary)]/30 bg-gradient-to-br from-[var(--color-accent-primary)]/5 to-transparent">
          <div className="text-[var(--color-accent-primary)] text-sm font-bold mb-2">
            {currentJob.status === 'in_progress' ? 'üîß IN PROGRESS' : 'üìç NEXT UP'}
          </div>
          <div className="text-xl font-bold mb-1">
            {currentJob.contact.firstName} {currentJob.contact.lastName}
          </div>
          <div className="text-[var(--color-text-secondary)] mb-3">{currentJob.description}</div>

          <div className="flex items-center gap-2 text-[var(--color-text-subtle)] text-sm mb-2">
            <MapPin className="w-4 h-4" />
            <span>{currentJob.contact.address}</span>
          </div>
          <div className="flex items-center gap-2 text-[var(--color-text-subtle)] text-sm mb-4">
            <Clock className="w-4 h-4" />
            <span>{formatTime(currentJob.scheduledStart)}</span>
          </div>

          <Link href={`/m/tech/job/${currentJob.id}`}>
            <BigButton
              icon={Wrench}
              label={currentJob.status === 'in_progress' ? 'CONTINUE JOB' : 'START JOB'}
              variant="success"
            />
          </Link>
        </Card>
      )}

      {/* Job List */}
      <h2 className="text-lg font-bold mb-3">Today's Schedule</h2>
      <div className="space-y-3">
        {jobs.map((job) => (
          <Link
            key={job.id}
            href={`/m/tech/job/${job.id}`}
            className="block"
          >
            <Card className="p-4 hover:shadow-card-hover transition-all duration-200">
              <div className="flex justify-between items-start">
                <div className="flex-1">
                  <div className="font-bold">
                    {job.contact.firstName} {job.contact.lastName}
                  </div>
                  <div className="text-[var(--color-text-secondary)] text-sm">{job.description}</div>
                  <div className="flex items-center gap-4 mt-2 text-[var(--color-text-subtle)] text-xs">
                    <span className="flex items-center gap-1">
                      <Clock className="w-3 h-3" />
                      {formatTime(job.scheduledStart)}
                    </span>
                    <span className={`px-2 py-0.5 rounded-full text-xs ${
                      job.status === 'completed' ? 'bg-green-500/20 text-green-400' :
                      job.status === 'in_progress' ? 'bg-[var(--color-accent-primary)]/20 text-[var(--color-accent-primary)]' :
                      'bg-[var(--color-bg-surface)] text-[var(--color-text-subtle)]'
                    }`}>
                      {job.status.replace('_', ' ')}
                    </span>
                  </div>
                </div>
                <ChevronRight className="w-5 h-5 text-[var(--color-text-subtle)]" />
              </div>
            </Card>
          </Link>
        ))}

        {jobs.length === 0 && (
          <Card className="text-center py-12">
            <div className="text-[var(--color-text-subtle)]">
              No jobs scheduled for today
            </div>
          </Card>
        )}
      </div>

      {/* Voice Command Button */}
      <VoiceButton />

      {/* Bottom Navigation */}
      <TechBottomNav />
    </div>
  )
}

"use client"

import * as React from "react"

import { cn } from "@/lib/utils"

/**
 * Professional Card with subtle depth
 * - Soft layered shadows for elevation (not flat)
 * - Subtle top-edge highlight (like glass catching light)
 * - Clean, conservative design for service industry
 */
function Card({ className, ...props }: React.ComponentProps<"div">) {
  return (
    <div
      data-slot="card"
      className={cn(
        // Base structure
        "relative flex flex-col gap-6 rounded-xl py-6 overflow-hidden",
        // Background - theme-aware for proper contrast
        "bg-[var(--card-bg)]",
        // Border - theme-aware
        "border-[var(--card-border)]",
        // Layered shadow for real depth (using shadow-card utility)
        "shadow-card",
        // Subtle top highlight - like light hitting top edge of a physical card
        "before:absolute before:inset-x-0 before:top-0 before:h-px",
        "before:bg-gradient-to-r before:from-transparent before:via-white/10 before:to-transparent",
        "before:pointer-events-none",
        // Smooth hover - slight lift, not dramatic
        "transition-all duration-200 ease-out",
        "hover:shadow-card-hover hover:-translate-y-px",
        className
      )}
      {...props}
    />
  )
}

/**
 * Stat Card - for KPIs and metrics (Tremor-inspired)
 * Professional, clean, shows data clearly
 */
function StatCard({
  className,
  title,
  value,
  change,
  changeType = "neutral",
  icon,
  ...props
}: React.ComponentProps<"div"> & {
  title?: string
  value?: string | number
  change?: string
  changeType?: "positive" | "negative" | "neutral"
  icon?: React.ReactNode
}) {
  const changeColors = {
    positive: "text-emerald-400",
    negative: "text-red-400",
    neutral: "text-slate-400",
  }

  return (
    <div
      data-slot="stat-card"
      className={cn(
        "relative flex flex-col gap-2 rounded-xl p-5 overflow-hidden",
        "bg-[var(--card-bg)]",
        "border-[var(--card-border)]",
        "shadow-card",
        "before:absolute before:inset-x-0 before:top-0 before:h-px",
        "before:bg-gradient-to-r before:from-transparent before:via-white/10 before:to-transparent",
        "before:pointer-events-none",
        className
      )}
      {...props}
    >
      <div className="flex items-center justify-between">
        <span className="text-sm font-medium text-slate-400">{title}</span>
        {icon && <span className="text-slate-500">{icon}</span>}
      </div>
      <div className="flex items-baseline gap-2">
        <span className="text-2xl font-semibold text-white tracking-tight">{value}</span>
        {change && (
          <span className={cn("text-sm font-medium", changeColors[changeType])}>
            {change}
          </span>
        )}
      </div>
    </div>
  )
}

function CardHeader({ className, ...props }: React.ComponentProps<"div">) {
  return (
    <div
      data-slot="card-header"
      className={cn(
        "@container/card-header grid auto-rows-min grid-rows-[auto_auto] items-start gap-2 px-6 has-data-[slot=card-action]:grid-cols-[1fr_auto] [.border-b]:pb-6",
        className
      )}
      {...props}
    />
  )
}

function CardTitle({ className, ...props }: React.ComponentProps<"div">) {
  return (
    <div
      data-slot="card-title"
      className={cn("leading-none font-semibold text-white relative z-10", className)}
      {...props}
    />
  )
}

function CardDescription({ className, ...props }: React.ComponentProps<"div">) {
  return (
    <div
      data-slot="card-description"
      className={cn("text-theme-subtle text-sm relative z-10", className)}
      {...props}
    />
  )
}

function CardAction({ className, ...props }: React.ComponentProps<"div">) {
  return (
    <div
      data-slot="card-action"
      className={cn(
        "col-start-2 row-span-2 row-start-1 self-start justify-self-end",
        className
      )}
      {...props}
    />
  )
}

function CardContent({ className, ...props }: React.ComponentProps<"div">) {
  return (
    <div
      data-slot="card-content"
      className={cn("px-6", className)}
      {...props}
    />
  )
}

function CardFooter({ className, ...props }: React.ComponentProps<"div">) {
  return (
    <div
      data-slot="card-footer"
      className={cn("flex items-center px-6 [.border-t]:pt-6", className)}
      {...props}
    />
  )
}

export {
