function BottomNav({ items }: { items: NavItem[] }) {
  const pathname = usePathname()

  return (
    <nav className="fixed bottom-0 left-0 right-0 bg-[var(--color-bg-secondary)]/95 backdrop-blur-md border-t border-[var(--color-border)] shadow-card safe-area-inset-bottom z-50">
      <div className="flex justify-around items-center h-16 relative">
        {/* Subtle top highlight for depth - same as desktop cards */}
        <div className="absolute top-0 left-0 right-0 h-px bg-gradient-to-r from-transparent via-white/10 to-transparent"></div>
        {items.map((item) => {
          const isActive = item.activePattern.test(pathname)
          const Icon = item.icon

          return (
            <Link
              key={item.href}
              href={item.href}
              className={`flex flex-col items-center justify-center flex-1 h-full transition-all duration-200 relative ${
                isActive
                  ? 'text-[var(--color-accent-primary)]'
                  : 'text-[var(--color-text-secondary)] hover:text-[var(--color-text-primary)] active:text-[var(--color-text-primary)]'
              }`}
            >
              {/* Active indicator with desktop-style styling */}
              {isActive && (
                <div className="absolute -top-1 w-8 h-0.5 bg-[var(--color-accent-primary)] rounded-full"></div>
              )}

              {/* Icon with subtle shadow when active - consistent with desktop */}
              <Icon className={`w-6 h-6 mb-1 transition-all duration-200 ${
                isActive ? 'scale-110' : ''
              }`} />
              <span className="text-xs font-medium">{item.label}</span>
            </Link>
          )
        })}
      </div>
    </nav>
  )
}

